// server/src/models/attribute.js
"use strict";
const { Model } = require("sequelize");

module.exports = (sequelize, DataTypes) => {
  class Attribute extends Model {
    static associate(models) {
      // 1-1 với post
      Attribute.hasOne(models.Post, { foreignKey: "attributesId", as: "post" });
    }
  }

  Attribute.init(
    {
      id: {
        type: DataTypes.STRING(36),
        primaryKey: true,
      },
      price: DataTypes.STRING,       // bạn để varchar
      acreage: DataTypes.STRING,     // bạn để varchar
      published: DataTypes.STRING,   // ngày đăng dạng text
      hashtag: DataTypes.STRING,     // gắn thẻ
    },
    {
      sequelize,
      modelName: "Attribute",
      tableName: "attributes",
    }
  );

  return Attribute;
};

