var _require = require('uuid'),
  uuidv4 = _require.v4;
module.exports = function (sequelize, DataTypes) {
  var Post = sequelize.define('Post', {
    id: {
      type: DataTypes.CHAR(36),
      primaryKey: true,
      defaultValue: function defaultValue() {
        return uuidv4();
      }
    },
    title: DataTypes.STRING,
    star: {
      type: DataTypes.TINYINT,
      defaultValue: 0
    },
    labelCode: {
      type: DataTypes.STRING(50),
      field: 'labelcode',
      allowNull: true
    },
    address: DataTypes.TEXT,
    province: DataTypes.STRING(100),
    district: DataTypes.STRING(100),
    ward: DataTypes.STRING(100),
    street: DataTypes.STRING(255),
    // 🔥 Trạng thái mới: pending, approved, expired, hidden, booking, booked
    status: {
      type: DataTypes.ENUM('pending', 'approved', 'expired', 'hidden', 'booking', 'booked'),
      defaultValue: 'pending'
    },
    categoryCode: {
      type: DataTypes.STRING(50),
      field: 'categoryCode'
    },
    description: DataTypes.TEXT,
    price: {
      type: DataTypes.INTEGER,
      allowNull: true
    },
    area: {
      type: DataTypes.INTEGER,
      allowNull: true
    },
