      field: "balance_before"
    },
    balanceAfter: {
      type: DataTypes.INTEGER.UNSIGNED,
      allowNull: false,
      field: "balance_after"
    },
    refType: {
      // ví dụ: 'POST', 'BOOKING', ...
      type: DataTypes.STRING(50),
      allowNull: true,
      field: "ref_type"
    },
    note: {
      type: DataTypes.STRING(255),
      allowNull: true
    },
    createdAt: {
      type: DataTypes.DATE,
      allowNull: false,
      field: "created_at",
      defaultValue: DataTypes.NOW
    }
  }, {
    tableName: "wallet_history",
    timestamps: false,
    // bảng chỉ có created_at
    underscored: true
  });
  WalletHistory.associate = function (models) {
    WalletHistory.belongsTo(models.User, {
      foreignKey: "userId",
      as: "user"
    });
  };
  return WalletHistory;
};
