// server/controllers/comment.js
const commentService = require("../services/comment");

exports.getCommentsByPost = async (req, res) => {
  try {
    const postId = req.params.id;
    const comments = await commentService.getCommentsByPost(postId);

    return res.json({
      success: true,
      data: comments,
    });
  } catch (err) {
    console.log("getCommentsByPost error >>>", err);
    return res.status(err.status || 500).json({
      success: false,
      message: err.message || "Internal server error",
    });
  }
};

exports.createComment = async (req, res) => {
  try {
    const userId = req.user?.id;
    if (!userId) {
      return res
        .status(401)
        .json({ success: false, message: "Không xác định userId" });
    }

    const postId = req.params.id;
    const comment = await commentService.createComment(postId, userId, req.body);

    return res.status(201).json({
      success: true,
      data: comment,
      message: "Tạo bình luận thành công",
    });
  } catch (err) {
    console.log("createComment error >>>", err);
    return res.status(err.status || 500).json({
      success: false,
      message: err.message || "Internal server error",
    });
  }
};

exports.deleteComment = async (req, res) => {
  try {
    const user = req.user;
    if (!user?.id) {
      return res
        .status(401)
        .json({ success: false, message: "Không xác định userId" });
    }

    const { commentId } = req.params;
    await commentService.deleteComment(commentId, user);

    return res.json({
      success: true,
      message: "Xoá bình luận thành công",
    });
  } catch (err) {
