sourcesContent:
// server/src/services/auth.js
import db from "../models/index.js";
import bcrypt from "bcryptjs";
import jwt from "jsonwebtoken";

const JWT_SECRET = process.env.JWT_SECRET || "secret123";
const JWT_EXPIRES_IN = "7d";

const exposeUser = (u) => ({
  id: u.id,
  name: u.name,
  phone: u.phone,
  role: Number(u.role ?? 0),
  money: Number(u.money ?? 0),
});

// ---------- ÄÄ‚NG KÃ ----------
export const registerService = async ({ name, phone, password, role }) => {
  // check trÃ¹ng sÄ‘t
  const existed = await db.User.findOne({ where: { phone } });
  if (existed) {
    return { err: 1, message: "Sá»‘ Ä‘iá»‡n thoáº¡i Ä‘Ã£ Ä‘Æ°á»£c Ä‘Äƒng kÃ½" };
  }

  // hash máº­t kháº©u
  const hashed = await bcrypt.hash(password, 10);

  // role há»£p lá»‡: 0 (ngÆ°á»i thuÃª), 1 (ngÆ°á»i cho thuÃª)
  // KHÃ”NG cho phÃ©p táº¡o role 2 (admin) tá»« client
  const safeRole = Number(role) === 1 ? 1 : 0;

  // táº¡o user
  const user = await db.User.create({
    name,
    phone,
    password: hashed,
    role: safeRole,
    money: 0,
  });

  // táº¡o token
  const token = jwt.sign(
    { id: user.id, phone: user.phone, role: safeRole },
    JWT_SECRET,
    { expiresIn: JWT_EXPIRES_IN }
  );

  return {
    err: 0,
    message: "ÄÄƒng kÃ½ thÃ nh cÃ´ng",
    user: exposeUser(user),
    token,
  };
};

// ---------- ÄÄ‚NG NHáº¬P ----------
export const loginService = async ({ phone, password }) => {
  const user = await db.User.findOne({ where: { phone } });
  if (!user) {
    return { err: 1, message: "Sá»‘ Ä‘iá»‡n thoáº¡i hoáº·c máº­t kháº©u khÃ´ng Ä‘Ãºng" };
  }

  const isMatch = await bcrypt.compare(password, user.password);
  if (!isMatch) {
    return { err: 1, message: "Sá»‘ Ä‘iá»‡n thoáº¡i hoáº·c máº­t kháº©u khÃ´ng Ä‘Ãºng" };
  }

  const token = jwt.sign(
    { id: user.id, phone: user.phone, role: Number(user.role ?? 0) },
    JWT_SECRET,
    { expiresIn: JWT_EXPIRES_IN }
  );

  return {
    err: 0,
    message: "ÄÄƒng nháº­p thÃ nh cÃ´ng",
    user: exposeUser(user),
    token,
  };
};

