sourcesContent:
// server/src/controllers/auth.js
import * as authService from "../services/auth.js";

/**
 * POST /api/auth/register
 * body: { name, phone, password, role } // role: 0 (ngÆ°á»i thuÃª), 1 (ngÆ°á»i cho thuÃª)
 */
export const register = async (req, res) => {
  try {
    const { name, phone, password, role } = req.body;

    if (!name || !phone || !password) {
      return res.status(400).json({ message: "All fields are required" });
    }

    // Chá»‰ cháº¥p nháº­n 0 hoáº·c 1 tá»« client. KhÃ´ng cho táº¡o admin á»Ÿ Ä‘Ã¢y.
    const safeRole = Number(role) === 1 ? 1 : 0;

    const response = await authService.registerService({
      name,
      phone,
      password,
      role: safeRole,
    });

    if (response.err) {
      // 409 khi trÃ¹ng SÄT, cÃ¡c case khÃ¡c 400
      const code = response.message?.includes("Ä‘Äƒng kÃ½")
        ? 409
        : 400;
      return res.status(code).json({ message: response.message });
    }

    // response.user sáº½ cÃ³ { id, name, phone, role, money }, kÃ¨m token
    return res.status(201).json(response);
  } catch (error) {
    console.error("Register error:", error);
    return res.status(500).json({ message: "Internal server error" });
  }
};

/**
 * POST /api/auth/login
 * body: { phone, password }
 */
export const login = async (req, res) => {
  try {
    const { phone, password } = req.body;

    if (!phone || !password) {
      return res.status(400).json({ message: "Vui lÃ²ng nháº­p Ä‘á»§ thÃ´ng tin" });
    }

    const response = await authService.loginService({ phone, password });

    if (response.err) {
      return res.status(401).json({ message: response.message });
    }

    // response.user sáº½ cÃ³ { id, name, phone, role, money }, kÃ¨m token
    return res.status(200).json(response);
  } catch (error) {
    console.error("Login error:", error);
    return res.status(500).json({ message: "Internal server error" });
  }
};

