// server/src/models/attribute.js
"use strict";
const { Model } = require("sequelize");

module.exports = (sequelize, DataTypes) => {
  class Attribute extends Model {
    static associate(models) {
      // 1-1 vá»›i post
      Attribute.hasOne(models.Post, { foreignKey: "attributesId", as: "post" });
    }
  }

  Attribute.init(
    {
      id: {
        type: DataTypes.STRING(36),
        primaryKey: true,
      },
      price: DataTypes.STRING,       // báº¡n Ä‘á»ƒ varchar
      acreage: DataTypes.STRING,     // báº¡n Ä‘á»ƒ varchar
      published: DataTypes.STRING,   // ngÃ y Ä‘Äƒng dáº¡ng text
      hashtag: DataTypes.STRING,     // gáº¯n tháº»
    },
    {
      sequelize,
      modelName: "Attribute",
      tableName: "attributes",
    }
  );

  return Attribute;
};

