const postService = require('../services/post');

// Táº¡o bÃ i Ä‘Äƒng má»›i + trá»« tiá»n nhÃ£n
exports.createPost = async (req, res) => {
  try {
    const userId = req.user?.id;
    if (!userId) {
      return res
        .status(401)
        .json({ success: false, message: 'KhÃ´ng xÃ¡c Ä‘á»‹nh userId' });
    }

    const result = await postService.createPost(req.body, userId);

    const post = result?.post || result;
    const charged = result?.charged ?? 0;
    const balance = result?.balance;

    return res.status(201).json({
      success: true,
      data: { id: post.id },
      charged,
      balance,
      message: 'Táº¡o bÃ i thÃ nh cÃ´ng',
    });
  } catch (err) {
    if (err.code === 'INSUFFICIENT_BALANCE') {
      return res.status(402).json({
        success: false,
        code: err.code,
        message: 'Sá»‘ dÆ° tÃ i khoáº£n khÃ´ng Ä‘á»§',
        needed: err.needed,
        balance: err.balance,
      });
    }

    console.log('createPost error >>>', err);
    return res
      .status(500)
      .json({ success: false, message: 'Internal server error' });
  }
};

// Láº¥y list bÃ i public (cÃ³ filter)
exports.getPosts = async (req, res) => {
  try {
    const posts = await postService.getPosts(req.query);
    return res.json({ success: true, data: posts });
  } catch (err) {
    return res.status(500).json({ success: false, message: err.message });
  }
};

// Láº¥y chi tiáº¿t 1 bÃ i
exports.getPost = async (req, res) => {
  try {
    const post = await postService.getPostById(req.params.id);
    return res.json({ success: true, data: post });
  } catch (err) {
    return res.status(404).json({ success: false, message: err.message });
  }
};

/** ðŸ”¥ Láº¤Y DANH SÃCH TIN Cá»¦A USER ÄANG ÄÄ‚NG NHáº¬P */
exports.getMyPosts = async (req, res) => {
  try {
    const userId = req.user?.id;
    if (!userId) {
      return res
        .status(401)
        .json({ success: false, message: 'KhÃ´ng xÃ¡c Ä‘á»‹nh userId' });
    }

    const posts = await postService.getPostsByUser(userId);

    return res.json({
      success: true,
      data: posts,
    });
  } catch (err) {
    console.log('getMyPosts error >>>', err);
    return res.status(500).json({
      success: false,
      message: err.message || 'Internal server error',
    });
  }
};

/** âœï¸ Cáº¬P NHáº¬T BÃ€I ÄÄ‚NG (KHÃ”NG TRá»ª TIá»€N, GIá»® NGUYÃŠN NHÃƒN) */
exports.updatePost = async (req, res) => {
  try {
    const userId = req.user?.id;
    if (!userId) {
      return res
        .status(401)
        .json({ success: false, message: 'KhÃ´ng xÃ¡c Ä‘á»‹nh userId' });
    }

    const result = await postService.updatePost(
      req.params.id,
      req.body,
      userId
    );
    const post = result?.post || result;

    return res.json({
      success: true,
      data: { id: post.id },
      message: 'Cáº­p nháº­t bÃ i thÃ nh cÃ´ng',
    });
  } catch (err) {
    console.log('updatePost error >>>', err);

    const status = err.status || 500;
    const message =
      err.message ||
      (status === 403
        ? 'Báº¡n khÃ´ng cÃ³ quyá»n sá»­a bÃ i Ä‘Äƒng nÃ y'
        : 'Internal server error');

    return res.status(status).json({
      success: false,
      message,
    });
  }
};

/** ðŸ’¡ Gáº®N / Äá»”I NHÃƒN CHO BÃ€I ÄÄ‚NG + TRá»ª TIá»€N */
exports.updateLabel = async (req, res) => {
  try {
    const userId = req.user?.id;
    if (!userId) {
      return res
        .status(401)
        .json({ success: false, message: 'KhÃ´ng xÃ¡c Ä‘á»‹nh userId' });
    }

    const postId = req.params.id;
    const { labelCode } = req.body;

    const result = await postService.updateLabel(postId, labelCode, userId);
    const post = result?.post || result;
    const charged = result?.charged ?? 0;
    const balance = result?.balance;

    return res.json({
      success: true,
      data: { id: post.id, labelCode: post.labelCode },
      charged,
      balance,
      message: 'Cáº­p nháº­t nhÃ£n thÃ nh cÃ´ng',
    });
  } catch (err) {
    if (err.code === 'INSUFFICIENT_BALANCE') {
      return res.status(402).json({
        success: false,
        code: err.code,
        message: 'Sá»‘ dÆ° tÃ i khoáº£n khÃ´ng Ä‘á»§',
        needed: err.needed,
        balance: err.balance,
      });
    }

    console.log('updateLabel error >>>', err);
    const status = err.status || 500;

    return res.status(status).json({
      success: false,
      message: err.message || 'Internal server error',
    });
  }
};

/** â±ï¸ GIA Háº N BÃ€I ÄÄ‚NG (tÄƒng sá»‘ ngÃ y star, trá»« tiá»n) */
exports.extendPost = async (req, res) => {
  try {
    const userId = req.user?.id;
    if (!userId) {
      return res
        .status(401)
        .json({ success: false, message: 'KhÃ´ng xÃ¡c Ä‘á»‹nh userId' });
    }

    const postId = req.params.id;
    const { days } = req.body;

    const result = await postService.extendPost(postId, days, userId);
    const post = result?.post || result;
    const charged = result?.charged ?? 0;
    const balance = result?.balance;

    return res.json({
      success: true,
      data: {
        id: post.id,
        star: post.star,
        status: post.status,
        createdAt: post.createdAt,
      },
      charged,
      balance,
      message: 'Gia háº¡n bÃ i Ä‘Äƒng thÃ nh cÃ´ng',
    });
  } catch (err) {
    if (err.code === 'INSUFFICIENT_BALANCE') {
      return res.status(402).json({
        success: false,
        code: err.code,
        message: 'Sá»‘ dÆ° tÃ i khoáº£n khÃ´ng Ä‘á»§',
        needed: err.needed,
        balance: err.balance,
      });
    }

    console.log('extendPost error >>>', err);
    const status = err.status || 500;

    return res.status(status).json({
      success: false,
      message: err.message || 'Internal server error',
    });
  }
};

/** ðŸ” ÄÄ‚NG Láº I BÃ€I ÄÄ‚NG (giá»¯ nguyÃªn id, tÃ­nh láº¡i phÃ­ nhÃ£n) */
exports.repostPost = async (req, res) => {
  try {
    const userId = req.user?.id;
    if (!userId) {
      return res
        .status(401)
        .json({ success: false, message: 'KhÃ´ng xÃ¡c Ä‘á»‹nh userId' });
    }

    const postId = req.params.id;
    const result = await postService.repostPost(postId, req.body, userId);

    const post = result?.post || result;
    const charged = result?.charged ?? 0;
    const balance = result?.balance;

    return res.json({
      success: true,
      data: { id: post.id },
      charged,
      balance,
      message: 'ÄÄƒng láº¡i bÃ i thÃ nh cÃ´ng',
    });
  } catch (err) {
    if (err.code === 'INSUFFICIENT_BALANCE') {
      return res.status(402).json({
        success: false,
        code: err.code,
        message: 'Sá»‘ dÆ° tÃ i khoáº£n khÃ´ng Ä‘á»§',
        needed: err.needed,
        balance: err.balance,
      });
