const { Op } = require('sequelize');
const db = require('../models');
const { Post, Image, Video, User, WalletHistory } = db;

/* ---------- cost nhÃ£n ---------- */
const LABEL_COST = {
  HOT: 50000,
  VIP1: 30000,
  VIP2: 20000,
  VIP3: 10000,
};

/* ---------- cost gia háº¡n ---------- */
const EXTEND_COST = {
  3: 15000,
  7: 30000,
  30: 135000,
};

/* ---------- helpers ---------- */
const PROVINCE_SLUG_MAP = {
  'ho-chi-minh': 'Há»“ ChÃ­ Minh',
  'ha-noi': 'HÃ  Ná»™i',
  'da-nang': 'ÄÃ  Náºµng',
  'binh-duong': 'BÃ¬nh DÆ°Æ¡ng',
};

// parse chuá»—i "min-max" thÃ nh sá»‘
function parseRange(s) {
  if (!s) return {};
  const [minRaw, maxRaw] = String(s).split('-');
  const min = Number(minRaw);
  const max = Number(maxRaw);
  return {
    min: Number.isFinite(min) ? min : undefined,
    max: Number.isFinite(max) ? max : undefined,
  };
}

/* ---------- helper háº¿t háº¡n: createdAt + star (ngÃ y) ---------- */
const EXPIRABLE_STATUSES = ['pending', 'approved', 'booking', 'booked'];

function calcExpireAt(createdAt, star) {
  if (!createdAt) return null;
  const days = Number(star || 0);
  if (!days) return null;
  return new Date(createdAt.getTime() + days * 24 * 60 * 60 * 1000);
}

async function ensureExpiredIfNeeded(post) {
  if (!post) return post;

  const status = post.status || 'pending';
  if (!EXPIRABLE_STATUSES.includes(status)) return post;

  const expireAt = calcExpireAt(post.createdAt, post.star);
  if (!expireAt) return post;

  if (expireAt.getTime() <= Date.now() && status !== 'expired') {
    post.status = 'expired';
    await post.save();
  }

  return post;
}

/* ---------- helper ghi lá»‹ch sá»­ vÃ­ ---------- */
async function logWalletHistory({
  t,
  userId,
  action,
  amountIn = 0,
  amountOut = 0,
  balanceBefore,
  balanceAfter,
  refType = null,
  note = null,
}) {
  try {
    if (!WalletHistory) return;

    const payload = {
      userId,
      action,
      amountIn,
      amountOut,
      balanceBefore,
      balanceAfter,
      refType,
      note,
    };

    const options = t ? { transaction: t } : {};
    await WalletHistory.create(payload, options);
  } catch (e) {
    // KhÃ´ng Ä‘á»ƒ lá»—i log lÃ m há»ng giao dá»‹ch chÃ­nh
    console.error('logWalletHistory error:', e.message);
  }
}

/* ============================================================
 *  createPost: Táº¡o bÃ i + trá»« tiá»n label + ghi wallet_history
 *  (luÃ´n táº¡o post má»›i, id má»›i)
 * ==========================================================*/
exports.createPost = async (payload, userId) => {
  const t = await db.sequelize.transaction();
  try {
    const {
      title,
      categoryCode,
      labelCode,
      description,
      address,
      province,
      district,
      ward,
      street,
      contact_name,
      contact_phone,
      price,
      area,
      // máº£ng features (vÃ­ dá»¥: ["parking","elevator"] hoáº·c ["Chá»— Ä‘á»ƒ xe",...])
      features = [],
      imageUrls = [],
      videoUrl = null,
      walletAction,
      action,
    } = payload;

    // Action Ä‘á»ƒ ghi lá»‹ch sá»­ vÃ­
    const historyAction = walletAction || action || 'POST_CREATE';

    // 1) TÃ­nh phÃ­ nhÃ£n
    const trimmedLabel = (labelCode || '').toUpperCase().trim();
    const cost = LABEL_COST[trimmedLabel] || 0;

    // 1.5) Äáº·t status + star theo rule
    //  - KhÃ´ng label:  status = pending, star = 1
    //  - CÃ³ label:     status = approved, star = 3
    const hasLabel = !!trimmedLabel;
    const initialStatus = hasLabel ? 'approved' : 'pending';
    const initialStar = hasLabel ? 3 : 1;

    // 2) Náº¿u cÃ³ phÃ­ -> lock user & trá»« tiá»n
    let newBalance;
    let balanceBefore = null;
    let balanceAfter = null;

    if (cost > 0) {
      const user = await User.findByPk(userId, {
        transaction: t,
        lock: t.LOCK.UPDATE,
      });
      const balance = Number(user?.money || 0);

      if (balance < cost) {
        await t.rollback();
        const err = new Error('Sá»‘ dÆ° tÃ i khoáº£n khÃ´ng Ä‘á»§');
        err.code = 'INSUFFICIENT_BALANCE';
        err.balance = balance;
        err.needed = cost;
        throw err;
      }

      balanceBefore = balance;
      newBalance = balance - cost;
      balanceAfter = newBalance;

      user.money = newBalance;
      await user.save({ transaction: t });
    }

    // 3) Táº¡o bÃ i Ä‘Äƒng
    const post = await Post.create(
      {
        title,
        star: initialStar,
        labelCode: trimmedLabel || null,
        address,
        province,
        district,
        ward,
        street,
        status: initialStatus,
        categoryCode,
        description,
        price: price ?? null,
        area: area ?? null,
        features,
        userId,
        contact_name,
        contact_phone,
      },
      { transaction: t }
    );

    // 4) áº¢nh
    if (Array.isArray(imageUrls) && imageUrls.length) {
      const rows = imageUrls.map((url, idx) => ({
        postId: post.id,
        url,
        isPrimary: idx === 0 ? 1 : 0,
        sortOrder: idx,
      }));
      await Image.bulkCreate(rows, { transaction: t });
    }

    // 5) Video
    if (videoUrl) {
      await Video.create({ postId: post.id, url: videoUrl }, { transaction: t });
    }

    // 6) Ghi lá»‹ch sá»­ vÃ­ náº¿u cÃ³ trá»« tiá»n
    if (cost > 0 && balanceBefore != null && balanceAfter != null) {
      await logWalletHistory({
        t,
        userId,
        action: historyAction, // POST_CREATE (máº·c Ä‘á»‹nh)
        amountIn: 0,
        amountOut: cost,
        balanceBefore,
        balanceAfter,
        refType: 'POST',
        note:
          historyAction === 'POST_REPOST'
            ? `ÄÄƒng láº¡i tin #${post.id}${trimmedLabel ? ` vá»›i nhÃ£n ${trimmedLabel}` : ''}`
            : `ÄÄƒng tin má»›i #${post.id}${trimmedLabel ? ` vá»›i nhÃ£n ${trimmedLabel}` : ''}`,
      });
    }

    await t.commit();
    return { post, charged: cost, balance: newBalance };
  } catch (err) {
    await t.rollback();
    throw err;
  }
};

/* ============================================================
 *  repostPost: ÄÄ‚NG Láº I tin (GIá»® NGUYÃŠN post.id)
 *  - Cáº­p nháº­t láº¡i ná»™i dung giá»‘ng createPost
 *  - Reset status, star, createdAt
 *  - TÃ­nh phÃ­ nhÃ£n, trá»« tiá»n & ghi wallet_history (POST_REPOST)
 * ==========================================================*/
exports.repostPost = async (postId, payload, userId) => {
  const t = await db.sequelize.transaction();
  try {
    const post = await Post.findOne({
      where: { id: postId, userId },
      transaction: t,
      lock: t.LOCK.UPDATE,
    });

    if (!post) {
      const err = new Error('KhÃ´ng tÃ¬m tháº¥y bÃ i Ä‘Äƒng');
      err.status = 404;
      throw err;
    }

    const {
      title,
      categoryCode,
      labelCode,
      description,
      address,
      province,
      district,
      ward,
      street,
      contact_name,
      contact_phone,
      price,
      area,
      features = [],
      imageUrls = [],
      videoUrl = null,
    } = payload;

    const trimmedLabel = (labelCode || '').toUpperCase().trim();
    const hasLabel = !!trimmedLabel;
    const cost = LABEL_COST[trimmedLabel] || 0;

    const initialStatus = hasLabel ? 'approved' : 'pending';
    const initialStar = hasLabel ? 3 : 1;

    let newBalance;
    let balanceBefore = null;
    let balanceAfter = null;

    if (cost > 0) {
      const user = await User.findByPk(userId, {
        transaction: t,
        lock: t.LOCK.UPDATE,
      });

      const balance = Number(user?.money || 0);
      if (balance < cost) {
        await t.rollback();
        const err = new Error('Sá»‘ dÆ° tÃ i khoáº£n khÃ´ng Ä‘á»§');
        err.code = 'INSUFFICIENT_BALANCE';
        err.balance = balance;
        err.needed = cost;
        throw err;
      }

      balanceBefore = balance;
      newBalance = balance - cost;
      balanceAfter = newBalance;

      user.money = newBalance;
      await user.save({ transaction: t });
    }

    // Cáº­p nháº­t láº¡i ná»™i dung bÃ i (giá»‘ng táº¡o má»›i, nhÆ°ng giá»¯ nguyÃªn id)
    post.title = title;
    post.categoryCode = categoryCode;
    post.description = description;
    post.address = address;
    post.province = province;
    post.district = district;
    post.ward = ward;
    post.street = street;
    post.contact_name = contact_name;
    post.contact_phone = contact_phone;
    post.price = price ?? null;
    post.area = area ?? null;
    post.features = features;
    post.labelCode = trimmedLabel || null;
    post.status = initialStatus;
    post.star = initialStar;
    post.createdAt = new Date(); // ngÃ y Ä‘Äƒng má»›i

    await post.save({ transaction: t });

    // Cáº­p nháº­t áº£nh: xoÃ¡ háº¿t rá»“i táº¡o láº¡i
    await Image.destroy({ where: { postId: post.id }, transaction: t });

    if (Array.isArray(imageUrls) && imageUrls.length) {
      const rows = imageUrls.map((url, idx) => ({
        postId: post.id,
        url,
        isPrimary: idx === 0 ? 1 : 0,
        sortOrder: idx,
      }));
      await Image.bulkCreate(rows, { transaction: t });
    }

    // Cáº­p nháº­t video: xoÃ¡ háº¿t rá»“i táº¡o láº¡i náº¿u cÃ³
    await Video.destroy({ where: { postId: post.id }, transaction: t });

    if (videoUrl) {
      await Video.create({ postId: post.id, url: videoUrl }, { transaction: t });
    }

    // Ghi log vÃ­ náº¿u cÃ³ trá»« tiá»n
    if (cost > 0 && balanceBefore != null && balanceAfter != null) {
      await logWalletHistory({
        t,
        userId,
        action: 'POST_REPOST',
        amountIn: 0,
        amountOut: cost,
        balanceBefore,
        balanceAfter,
        refType: 'POST',
        note: `ÄÄƒng láº¡i tin #${post.id}${trimmedLabel ? ` vá»›i nhÃ£n ${trimmedLabel}` : ''}`,
      });
    }

    await t.commit();
    return { post, charged: cost, balance: newBalance };
  } catch (err) {
    await t.rollback();
    throw err;
  }
};

/* ============================================================
 *  getPosts: Láº¥y danh sÃ¡ch bÃ i cÃ³ filter
 * ==========================================================*/
exports.getPosts = async (query = {}) => {
  const where = {};
  const andConditions = [];

  /* ---- tráº¡ng thÃ¡i bÃ i ---- */
  if (query.status) {
    const s = String(query.status);
    if (s === 'public') {
      where.status = { [Op.in]: ['pending', 'approved'] };
    } else {
      where.status = s;
    }
  }

  /* ---- danh má»¥c ---- */
  if (query.category) {
    where.categoryCode = String(query.category).toUpperCase();
  }

  /* ---- tá»‰nh ---- */
  if (query.province) {
    const raw = String(query.province);
    const pv =
      PROVINCE_SLUG_MAP[raw.toLowerCase()] ||
      raw;
    where.province = { [Op.like]: `%${pv}%` };
  }

  /* ---- quáº­n / huyá»‡n, phÆ°á»ng / xÃ£ ---- */
  if (query.district) {
    where.district = { [Op.like]: `%${query.district}%` };
  }

  if (query.ward) {
    where.ward = { [Op.like]: `%${query.ward}%` };
  }

  /* ---- khoáº£ng giÃ¡ ---- */
  if (query.price) {
    const { min, max } = parseRange(query.price);
    if (min && max) where.price = { [Op.between]: [min, max] };
    else if (min) where.price = { [Op.gte]: min };
    else if (max) where.price = { [Op.lte]: max };
  }

  /* ---- khoáº£ng diá»‡n tÃ­ch ---- */
  if (query.area) {
    const { min, max } = parseRange(query.area);
    if (min && max) where.area = { [Op.between]: [min, max] };
    else if (min) where.area = { [Op.gte]: min };
    else if (max) where.area = { [Op.lte]: max };
  }

  /* ---- features ---- */
  if (query.features) {
    const feats = Array.isArray(query.features)
      ? query.features
      : String(query.features)
          .split(',')
          .map((s) => s.trim())
          .filter(Boolean);

    if (feats.length) {
      feats.forEach((feat) => {
        andConditions.push({
          features: { [Op.like]: `%${feat}%` },
        });
      });
    }
  }

  if (andConditions.length) {
    where[Op.and] = andConditions;
  }

  const rows = await Post.findAll({
    where,
    order: [['createdAt', 'DESC']],
    include: [
      {
        model: Image,
        as: 'images',
        attributes: ['id', 'url', 'isPrimary', 'sortOrder', 'createdAt'],
        required: false,
      },
      {
        model: Video,
        as: 'videos',
        attributes: ['id', 'url', 'createdAt'],
        required: false,
      },
    ],
    limit: 50,
  });

  for (const p of rows) {
    // eslint-disable-next-line no-await-in-loop
    await ensureExpiredIfNeeded(p);
  }

  return rows.map((p) => {
    const imgs = Array.isArray(p.images) ? p.images : [];
    const vids = Array.isArray(p.videos) ? p.videos : [];
    const primary =
      imgs.find((i) => (i.isPrimary ?? i.is_primary) === 1) || imgs[0];

    return {
      id: p.id,
      userId: p.userId,
      title: p.title,
      description: p.description,
      price: p.price,
      area: p.area,
      province: p.province,
      district: p.district,
      ward: p.ward,
      street: p.street,
      address: p.address,
      categoryCode: p.categoryCode,
      labelCode: p.labelCode || null,
      star: p.star || 0,
      status: p.status || 'pending',
      contact_name: p.contact_name || '',
      contact_phone: p.contact_phone || '',
      createdAt: p.createdAt,
      features: p.features,
      coverImage: primary ? primary.url : null,
      videoUrl: vids[0]?.url || null,
      images: imgs.map((i) => ({
        url: i.url,
        isPrimary: i.isPrimary ?? i.is_primary,
        sortOrder: i.sortOrder ?? i.sort_order,
      })),
      videos: vids.map((v) => ({ url: v.url })),
    };
  });
};

/* ============================================================
 *  getPostById: láº¥y chi tiáº¿t 1 bÃ i
 * ==========================================================*/
exports.getPostById = async (id) => {
  const post = await Post.findByPk(id, {
    include: [
      {
        model: Image,
        as: 'images',
        attributes: ['id', 'url', 'isPrimary', 'sortOrder'],
      },
      {
        model: Video,
        as: 'videos',
        attributes: ['id', 'url'],
      },
    ],
  });

  if (!post) {
    const err = new Error('Post not found');
    err.status = 404;
    throw err;
  }

  await ensureExpiredIfNeeded(post);

  return post;
};

/* ============================================================
 *  updatePost: cáº­p nháº­t bÃ i (khÃ´ng trá»« tiá»n, giá»¯ nguyÃªn label)
 * ==========================================================*/
exports.updatePost = async (postId, payload, userId) => {
  const t = await db.sequelize.transaction();
  try {
    const post = await Post.findByPk(postId, { transaction: t });

    if (!post) {
      const err = new Error('Post not found');
      err.status = 404;
      throw err;
    }

    if (Number(post.userId) !== Number(userId)) {
      const err = new Error('Forbidden');
      err.status = 403;
      throw err;
    }

    const {
      title,
      categoryCode,
      description,
      address,
      province,
      district,
      ward,
      street,
      contact_name,
      contact_phone,
      price,
      area,
      features = [],
      imageUrls = [],
      videoUrl = null,
    } = payload;

    post.title = title;
    post.categoryCode = categoryCode;
    post.description = description;
    post.address = address;
    post.province = province;
    post.district = district;
    post.ward = ward;
    post.street = street;
    post.contact_name = contact_name;
    post.contact_phone = contact_phone;
    post.price = price ?? null;
    post.area = area ?? null;
    post.features = features;

    await post.save({ transaction: t });

    await Image.destroy({ where: { postId: post.id }, transaction: t });

    if (Array.isArray(imageUrls) && imageUrls.length) {
      const rows = imageUrls.map((url, idx) => ({
        postId: post.id,
        url,
        isPrimary: idx === 0 ? 1 : 0,
        sortOrder: idx,
      }));
      await Image.bulkCreate(rows, { transaction: t });
    }

    await Video.destroy({ where: { postId: post.id }, transaction: t });

    if (videoUrl) {
      await Video.create({ postId: post.id, url: videoUrl }, { transaction: t });
    }

    await t.commit();
    return { post };
  } catch (err) {
    await t.rollback();
    throw err;
  }
};

/* ============================================================
 *  updateLabel: gáº¯n / Ä‘á»•i nhÃ£n cho bÃ i Ä‘Äƒng + log vÃ­
 * ==========================================================*/
exports.updateLabel = async (postId, labelCode, userId) => {
  const t = await db.sequelize.transaction();
  try {
    const post = await Post.findOne({
      where: { id: postId, userId },
      transaction: t,
      lock: t.LOCK.UPDATE,
    });

    if (!post) {
      const err = new Error('KhÃ´ng tÃ¬m tháº¥y bÃ i Ä‘Äƒng');
      err.status = 404;
      throw err;
    }

    const newLabel = (labelCode || '').toUpperCase().trim();
    const oldLabel = (post.labelCode || '').toUpperCase().trim();

    let cost = 0;
    let newBalance;
    let balanceBefore = null;
    let balanceAfter = null;

    if (newLabel !== oldLabel) {
      const labelCost = LABEL_COST[newLabel] || 0;
      cost = labelCost;

      if (cost > 0) {
        const user = await User.findByPk(userId, {
          transaction: t,
          lock: t.LOCK.UPDATE,
        });
        const balance = Number(user?.money || 0);

        if (balance < cost) {
          await t.rollback();
          const err = new Error('Sá»‘ dÆ° tÃ i khoáº£n khÃ´ng Ä‘á»§');
          err.code = 'INSUFFICIENT_BALANCE';
          err.balance = balance;
          err.needed = cost;
          throw err;
        }

        balanceBefore = balance;
        newBalance = balance - cost;
        balanceAfter = newBalance;

        user.money = newBalance;
        await user.save({ transaction: t });
      }

      post.labelCode = newLabel || null;
      await post.save({ transaction: t });
    }

    if (cost > 0 && balanceBefore != null && balanceAfter != null) {
      await logWalletHistory({
        t,
        userId,
        action: 'POST_LABEL',
        amountIn: 0,
        amountOut: cost,
        balanceBefore,
        balanceAfter,
        refType: 'POST',
        note: `Gáº¯n nhÃ£n ${newLabel || '(bá» nhÃ£n)'} cho bÃ i #${post.id}`,
      });
    }

    await t.commit();
    return { post, charged: cost, balance: newBalance };
  } catch (err) {
    await t.rollback();
    throw err;
  }
};

/* ============================================================
 *  extendPost: gia háº¡n bÃ i Ä‘Äƒng (tÄƒng star, trá»« tiá»n) + log vÃ­
 * ==========================================================*/
exports.extendPost = async (postId, days, userId) => {
  const t = await db.sequelize.transaction();
  try {
    const extendDays = Number(days);
    if (![3, 7, 30].includes(extendDays)) {
      const err = new Error('Sá»‘ ngÃ y gia háº¡n khÃ´ng há»£p lá»‡');
      err.status = 400;
      throw err;
    }

    const post = await Post.findOne({
      where: { id: postId, userId },
      transaction: t,
      lock: t.LOCK.UPDATE,
    });

    if (!post) {
      const err = new Error('KhÃ´ng tÃ¬m tháº¥y bÃ i Ä‘Äƒng');
      err.status = 404;
      throw err;
    }

    const cost = EXTEND_COST[extendDays] || 0;
    let newBalance;
    let balanceBefore = null;
    let balanceAfter = null;

    if (cost > 0) {
      const user = await User.findByPk(userId, {
        transaction: t,
        lock: t.LOCK.UPDATE,
      });
      const balance = Number(user?.money || 0);

      if (balance < cost) {
        await t.rollback();
        const err = new Error('Sá»‘ dÆ° tÃ i khoáº£n khÃ´ng Ä‘á»§');
        err.code = 'INSUFFICIENT_BALANCE';
        err.balance = balance;
        err.needed = cost;
        throw err;
      }

      balanceBefore = balance;
      newBalance = balance - cost;
      balanceAfter = newBalance;

      user.money = newBalance;
      await user.save({ transaction: t });
    }

    const oldStar = Number(post.star || 0);
    const newStar = oldStar + extendDays;

    post.star = newStar;

    if (post.status === 'expired') {
      post.status = 'approved';
    }

    await post.save({ transaction: t });

    if (cost > 0 && balanceBefore != null && balanceAfter != null) {
      await logWalletHistory({
        t,
        userId,
        action: 'POST_EXTEND',
        amountIn: 0,
        amountOut: cost,
        balanceBefore,
        balanceAfter,
        refType: 'POST',
        note: `Gia háº¡n +${extendDays} ngÃ y cho bÃ i #${post.id}`,
      });
    }

    await t.commit();
    return { post, charged: cost, balance: newBalance };
  } catch (err) {
    await t.rollback();
    throw err;
  }
};

/* ============================================================
 *  getPostsByUser: list bÃ i theo user
 * ==========================================================*/
exports.getPostsByUser = async (userId) => {
  const rows = await Post.findAll({
    where: { userId },
    order: [['createdAt', 'DESC']],
    include: [
      {
        model: Image,
        as: 'images',
        attributes: ['id', 'url', 'isPrimary', 'sortOrder', 'createdAt'],
        required: false,
      },
      {
        model: Video,
        as: 'videos',
        attributes: ['id', 'url', 'createdAt'],
        required: false,
      },
    ],
  });

  for (const p of rows) {
    // eslint-disable-next-line no-await-in-loop
    await ensureExpiredIfNeeded(p);
  }

  return rows.map((p) => {
    const imgs = Array.isArray(p.images) ? p.images : [];
    const vids = Array.isArray(p.videos) ? p.videos : [];
    const primary =
      imgs.find((i) => (i.isPrimary ?? i.is_primary) === 1) || imgs[0];

    return {
      id: p.id,
      userId: p.userId,
      title: p.title,
      description: p.description,
      price: p.price,
      area: p.area,
      province: p.province,
      district: p.district,
      ward: p.ward,
      street: p.street,
      address: p.address,
      categoryCode: p.categoryCode,
      labelCode: p.labelCode || null,
      star: p.star || 0,
      status: p.status || 'pending',
      contact_name: p.contact_name || '',
      contact_phone: p.contact_phone || '',
      createdAt: p.createdAt,
      coverImage: primary ? primary.url : null,
      videoUrl: vids[0]?.url || null,
      images: imgs.map((i) => ({
        url: i.url,
        isPrimary: i.isPrimary ?? i.is_primary,
        sortOrder: i.sortOrder ?? i.sort_order,
      })),
      videos: vids.map((v) => ({ url: v.url })),
    };
  });
};

/* ============================================================
 *  hidePost: áº¨n bÃ i Ä‘Äƒng (chá»§ bÃ i)
 * ==========================================================*/
exports.hidePost = async (postId, userId) => {
  const post = await Post.findOne({
    where: { id: postId, userId },
  });

  if (!post) {
    const err = new Error('KhÃ´ng tÃ¬m tháº¥y bÃ i Ä‘Äƒng');
    err.status = 404;
    throw err;
  }

  post.status = 'hidden';
  await post.save();

  return post;
};

/* ============================================================
 *  CÃ¡c hÃ m dÃ nh riÃªng cho ADMIN
 * ==========================================================*/

exports.approvePostByAdmin = async (postId) => {
  const post = await Post.findByPk(postId);
  if (!post) return null;

  post.status = 'approved';
  post.star = 3;

  await post.save();
  return post;
};

exports.deletePostByAdmin = async (postId) => {
  const deletedCount = await Post.destroy({ where: { id: postId } });
  return { success: deletedCount > 0 };
};

