// server/src/models/wallet_history.js
module.exports = (sequelize, DataTypes) => {
  const WalletHistory = sequelize.define(
    "WalletHistory",
    {
      id: {
        type: DataTypes.BIGINT.UNSIGNED,
        primaryKey: true,
        autoIncrement: true,
      },

      userId: {
        type: DataTypes.BIGINT.UNSIGNED,
        allowNull: false,
        field: "user_id",
      },

      action: {
        // CÃ¡c action tiá»n tá»‡
        type: DataTypes.ENUM(
          "POST_CREATE",      // ÄÄƒng tin má»›i (cÃ³ trá»« tiá»n nhÃ£n)
          "POST_LABEL",       // Gáº¯n / Ä‘á»•i nhÃ£n
          "POST_EXTEND",      // Gia háº¡n
          "POST_REPOST",      // ÄÄƒng láº¡i
          "DEPOSIT",          // Náº¡p tiá»n
          "WITHDRAW",         // RÃºt tiá»n
          "BOOKING",          // Äáº·t phÃ²ng
          "REFUND",           // HoÃ n tiá»n
          "RECEIVE_DEPOSIT"   // Nháº­n tiá»n cá»c
        ),
        allowNull: false,
      },

      amountIn: {
        type: DataTypes.INTEGER.UNSIGNED,
        allowNull: false,
        defaultValue: 0,
        field: "amount_in",
      },

      amountOut: {
        type: DataTypes.INTEGER.UNSIGNED,
        allowNull: false,
        defaultValue: 0,
        field: "amount_out",
      },

      balanceBefore: {
        type: DataTypes.INTEGER.UNSIGNED,
        allowNull: false,
        field: "balance_before",
      },

      balanceAfter: {
        type: DataTypes.INTEGER.UNSIGNED,
        allowNull: false,
        field: "balance_after",
      },

      refType: {
        // vÃ­ dá»¥: 'POST', 'BOOKING', ...
        type: DataTypes.STRING(50),
        allowNull: true,
        field: "ref_type",
      },

      note: {
        type: DataTypes.STRING(255),
        allowNull: true,
      },

      createdAt: {
        type: DataTypes.DATE,
        allowNull: false,
        field: "created_at",
        defaultValue: DataTypes.NOW,
      },
    },
    {
      tableName: "wallet_history",
      timestamps: false, // báº£ng chá»‰ cÃ³ created_at
      underscored: true,
    }
  );

  WalletHistory.associate = (models) => {
    WalletHistory.belongsTo(models.User, {
      foreignKey: "userId",
      as: "user",
    });
  };

  return WalletHistory;
};
